mapper.select:
      SELECT
      a.*
      FROM
      recogs a

      WHERE  1=1

          AND a.batchid = #{batchid}


          AND a.status = #{status}


          AND a.result = #{result}


          AND a.manualresult = #{manualresult}


          AND a.mobile = #{mobile}


    ORDER BY
	a.seqid ASC


		LIMIT #{start}, #{pagesize}

  	  SELECT
		COUNT(*)
      FROM
      recogs a

      WHERE  1=1

          AND a.batchid = #{batchid}


          AND a.status = #{status}


          AND a.result = #{result}


          AND a.manualresult = #{manualresult}


          AND a.mobile = #{mobile}

SELECT
a.*
FROM
batchlog a

WHERE  1=1

    AND a.merchid = #{merchid}


    AND a.batchid = #{batchid}


ORDER BY
a.seqid DESC


    LIMIT #{start}, #{pagesize}

SELECT COUNT(*) FROM batchlog a
WHERE 1=1
AND a.merchid = #{merchid}
AND a.batchid = #{batchid}

SELECT DISTINCT a.* FROM
(
SELECT DISTINCT ptel as phone, caseno FROM zncontact  WHERE importbatchid=#{batchid}
UNION
SELECT DISTINCT ptel1 as phone, caseno FROM zncontact  WHERE importbatchid=#{batchid}
) as a  WHERE ISNULL(a.phone)=FALSE AND a.phone <>  ''
ORDER BY a.caseno
